mapper.update:
update vmes_customer_invoice
set isdefault = #{isdefault}
where customer_id = #{customerId}

mapper.select:
select id id, customer_id customerId, company_name companyName, invoice_number invoiceNumber, address address,
bank_name bankName, bank_code bankCode, phone phone, mobile mobile, isdefault isdefault, remark remark, cuser cuser, cdate cdate, uuser uuser, udate udate,
isdisable isdisable from vmes_customer_invoice
and id <> #{id}
and id = #{id}
and id in (${ids})
and customer_id = #{customerId}
and isdefault = #{isdefault}
and ${queryStr}
order by ${orderStr}

select invoice.id id, invoice.customer_id customerId, invoice.company_name companyName, invoice.invoice_number invoiceNumber, invoice.address address,
invoice.bank_name bankName, invoice.bank_code bankCode, invoice.phone phone, invoice.mobile mobile, invoice.isdefault isdefault, case when isdefault='1' then '是' when isdefault='0' then '否' else null end isdefaultStr, invoice.remark remark, invoice.cuser cuser, invoice.cdate cdate, invoice.uuser uuser, invoice.udate udate,
invoice.isdisable isdisable from vmes_customer_invoice invoice left join vmes_customer customer on customer.id = invoice.customer_id
and invoice.company_name = #{name}
and invoice.invoice_number = #{code}
and invoice.customer_id = #{customerId}
and customer.company_id LIKE CONCAT(CONCAT('%', #{currentCompanyId}),'%')
and ${queryStr}
order by ${orderStr}
