mapper.select:
select a.id,a.code,a.name,a.name_en,a.cdate,a.cuser,a.type,a.typeName from (
select id, code, name, name_en, DATE_FORMAT(cdate,'%Y-%m-%d %T') cdate, cuser, '1' type, '虚拟库' typeName from vmes_warehouse where path_id like '%56f5e83dcb9911e884ad00163e105f05%' and layer = 2
and company_id=#{currentCompanyId}
UNION ALL
select id, code, name, name_en, DATE_FORMAT(cdate,'%Y-%m-%d %T') cdate, cuser, '2' type, '组装区' typeName from vmes_warehouse where path_id like '%2d75e49bcb9911e884ad00163e105f05%' and layer >= 3
and company_id=#{currentCompanyId}
UNION ALL
select cust.id, cust.code, cust.name, cust.name_en, DATE_FORMAT(cust.cdate,'%Y-%m-%d %T') cdate, cust.cuser, CASE WHEN genre = 'df7cb67fca4148bc9632c908e4a7fdea' THEN '3' WHEN genre = '49c0a7ebcb4c4175bd5195837a6a9a13' THEN '4' ELSE null END type, genre.name typeName from vmes_customer cust left join vmes_dictionary genre on cust.genre = genre.id where cust.isdisable = '1'
and cust.company_id=#{currentCompanyId}
UNION ALL
select id, code, name, name_en, DATE_FORMAT(cdate,'%Y-%m-%d %T') cdate, cuser, '5' type, '部门' typeName from vmes_department where layer = 2
and id_1=#{currentCompanyId}
and dept_type=#{deptType}
) a
and a.code=#{code}
and a.name=#{name}
and a.type=#{type}
and ${queryStr}
order by ${orderStr}
