mapper.update:
update
set isdisable = '0' ,udate = now() where id in
#{item}

mapper.select:
select 
from 



        where 1=2







                         and id <> #{id} 


                        and id = #{id}




                and id in (${ids})


                and code LIKE CONCAT('%',CONCAT(#{code}),'%')


                and source_code LIKE CONCAT('%',CONCAT(#{sourceCode}),'%')


                and name LIKE CONCAT('%',CONCAT(#{name}),'%')


                and company_id=#{companyId}


                and genre=#{genre}



                and spec LIKE CONCAT(CONCAT(#{spec}),'%')


                and unit=#{unit}


                and type=#{type}


                and isdisable=#{isdisable}




                and ${queryStr}





    order by ${orderStr}

mapper.update:
update vmes_product set stock_count = ${stockCount}, uuser = #{uuser}, udate = now(), version = version + 1
and id=#{id}
and version=${version}
