mapper.select:
select uuid() as id,
a.id as userID, a.employ_id as userEmployID, a.dept_id as userDeptID, a.company_id as userCompanyID, a.user_code as userCode,
a.password as userPassword, a.open_id as userOpenID, a.mobile as userMobile, a.email as userEmail, a.user_name as userName,
a.isdisable as userIsdisable, a.cdate as userCdate, a.cuser as userCuser, a.udate as userUdate, a.uuser as userUuser,
a.user_type as userType, a.column_1 as userColumn1, a.column_2 as userColumn2, a.column_3 as userColumn3,
b.id as employID, b.code as employCode, b.type as employType, b.user_id as employUserID, b.company_id as employCompanyID,
b.name as employName, b.name_en as employNameEn, b.photo, b.icon, b.mobile,
b.email, b.sex, b.birthday, b.leave_date as leaveDate, b.entry_date as entryDate,
b.native_place as nativePlace, b.political, b.identity_number as identityNumber, b.marital, b.isdisable as employIsdisable,
b.cdate as employCdate, b.cuser as employCuser, b.udate as employUdate, b.uuser as employUuser, b.remark as employRemark,
b.isOpenUser as isOpenUser, b.column_1 as employColumn1, b.column_2 as employColumn2, b.column_3 as employColumn3 from vmes_user a left join vmes_employee b on a.employ_id = b.id
where 1=2
and a.id = #{userID}
and a.company_id = #{userCompanyID}
and a.user_code = #{userCode}
and a.password = #{userPassword}
and a.mobile = #{userMobile}
and a.email = #{userEmail}
and a.isdisable = #{userIsdisable}
and b.id = #{employID}
and b.company_id = #{employCompanyID}
and b.type = #{employType}
and b.code = #{employCode}
and b.name =#{employName}
and b.name_en = #{employNameEn}
and b.mobile = #{mobile}
and b.email = #{email}
and b.isdisable = #{employIsdisable}
and ${queryStr}
order by ${orderStr}
